import styled, { css } from "styled-components";

const shrinkLabelStyles = css`
  top: -14px;
  font-size: 12px;
  color: $main-color;
`;

export const GroupContainer = styled.div`
  position: relative;
  margin: 45px 0;
`;

export const InputContainer = styled.input`
  background: none;
  background-color: #fff;
  color: $sub-color;
  font-size: 18px;
  padding: 10px 10px 10px 5px;
  display: block;
  width: 100%;
  border: none;
  border-radius: 0;
  border-bottom: 1px solid $sub-color;
  margin: 25px 0;

  &:focus {
    outline: none;
  }

  &:focus ~ -label {
    ${shrinkLabelStyles}
  }
`;

export const FormInputLabel = styled.label`
  color: $sub-color;
  font-size: 16px;
  font-weight: normal;
  position: absolute;
  pointer-events: none;
  left: 5px;
  top: 10px;
  transition: 250ms ease all;

  &.shrink {
    ${shrinkLabelStyles}
  }
`;
